graphql.query:
mutation Login($email: String!, $password: String!) {
  loginWithEmail(email: $email, password: $password) {
    messages {
      code
      field
      message
      options {
        key
        value
      }
      template
    }
    result {
      token {
        access
        refresh
      }
      user {
        status
        email
        emailVerified
        id
        userEducationCount
        userExperienceCount
        userTagCount
        parentId
        phoneNumber
        insertedAt
        appDownloaded
        phoneNumberVerified
        familyMembers {
          family {
            status
            title
            id
            familyMembers {
              status
              id
            }
          }
          status
          role
          id
        }
        roles
        role
        permissions
        username
        zohoRecruitId
        zohoCrmId
        chatUserId
        publicUserId
        appLastActiveData {
          lastActiveDatetime
          isActiveInLastFifteenDays
          deviceType
        }
        entityTags {
          entityId
          entityType
          id
          value
        }
        roleAssignments {
          activationDate
          country
          expirationDate
          id
          isArchived
          module
          role
          userId
        }
        profile {
          avatarUrl
          bio
          dob
          lName
          fName
          fullName
          timezoneData {
            zoneName
            id
          }
          gender
          location {
            country
          }
          userProfileLanguages {
            language {
              name
            }
            listening
            reading
            speaking
            writing
          }
          userSocialLinks {
            platform
            url
          }
        }
        country {
          emoji
          name
          id
        }
        currentLocation {
          emoji
          name
        }
        host {
          id
          supportChatGroup {
            clubMembers {
              user {
                id
              }
            }
            channelUrl
          }
        }
        upvoteCounts
        userMembership {
          membership {
            title
          }
        }
        profile {
          avatarUrl
          bio
          dob
          lName
          fName
          fullName
          timezoneData {
            zoneName
            id
          }
          gender
          location {
            country
          }
          userProfileLanguages {
            language {
              name
            }
            listening
            reading
            speaking
            writing
          }
          userSocialLinks {
            platform
            url
          }
        }
      }
    }
    successful
  }
}
